Service.method:

var objectURLList = [];

// Regexp to retrieve host, port and seokeyword. Context path and seo pattern path are stripped.
var pattern  = new RegExp("(http[s]?:\\/\\/[a-z.A-Z0-9]+)(:[\\d]+)?(?:[\\/][\\w\\d\\-_]*)*(\\/[\\w\\d\\-_]+)[\\/]?$");

for (var i = 0; i < dataElement.childNodes.length; i++) {
	var e = dataElement.childNodes[i];
	if (e.nodeName == "object") {
		var url = {};
		for (var j = 0; j < e.childNodes.length; j++) {
			var childElement = e.childNodes[j];
			if (childElement.nodeName == "url") {
				url.text = StringUtil.Singleton.trim(XMLUtil.getNodeValue(childElement), StringUtil.Singleton.trimWhiteSpaceAndLineFeed);
			}
			else if (childElement.nodeName == "alias") {
				url.alias = XMLUtil.getNodeValue(childElement);
			}
			else if (childElement.nodeName == "storeId"){
				url.storeId = XMLUtil.getNodeValue(childElement);
			}
		}
		var storeConfig = StoreUtil.Singleton.storeMap[url.storeId];
		if (storeConfig.remoteStoreServerConfig.headlessStore == "TRUE"){

			var contextPath = "";
			if (storeConfig) {
				contextPath = storeConfig.remoteStoreServerConfig.remoteWebServerContextPath? storeConfig.remoteStoreServerConfig.remoteWebServerContextPath : "";
			}
			var matches = url.text.match(pattern);
			if (matches){
				matches.shift();
				matches = matches.filter(function(e){return e !== undefined});
				var seoKeyword = matches.pop();

				//keep the context path. Remove the seo pattern langauge token...
				url.text = matches.join('').concat(contextPath).concat(seoKeyword);

				// Angular Stockholm store local dev env running on http instead of https
				if (matches[0] && matches[0].toLowerCase().indexOf("localhost") > -1){
					url.text = url.text.replace("https", "http");
				}
			}
			url.text = url.text + "?storeId=" + url.storeId;
		}
		objectURLList.push(url);
	}
}
if (StoreSearchUrlResultDisplayDialog.Singleton.storeUrlDisplayDialogActive) {
	StoreSearchUrlResultDisplayDialog.Singleton.addUrl(objectURLList);
}
else if (StorePreviewDialog.Singleton.storePreviewDialogActive) {
	StorePreviewDialog.Singleton.populateObjectURLList(objectURLList);
}
else {
	if (objectURLList.length == 1) {
		var startDate = null;
		for (var i = 0; i < this.serviceParams.length; i++) {
			if (this.serviceParams[i].parameterName == "previewStartDate") {
				startDate = this.serviceParams[i].value;
			}
		}
		var url = objectURLList[0];
		var storeConfig = StoreUtil.Singleton.storeMap[url.storeId];
		require(["cmc/foundation/StorePreviewUtil"], function(StorePreviewUtil) {
			StorePreviewUtil.Singleton.launchStorePreview(url.text, requestParent.model.oEditor.localContext, {
				start: startDate,
				headlessStore: storeConfig.remoteStoreServerConfig.headlessStore == "TRUE"
			});
		});
	}
	else {
		requestParent.model.oEditor.openStorePreviewDialog();
	}
}


if (typeof(del) == "undefined") del = null;
var enabled = true;
if (this.enablementCondition) {
	if (del != null) {
		this.enablementCondition.registerDelegate(o, del);
	}
	enabled = this.enablementCondition.isEnabled(o);
}
return enabled;
